00543 
00553 bool APPCFG::SetAutomaticTraceTargets()
00554 {
00555   bool status = false;
00556 
00557   for(int c=0;c<XTRACE_MAXNTARGETS; c++)
00558     {
00559       XSTRING* debugcfg = GetTraceTarget(c);
00560       if(debugcfg)
00561         {
00562           if(!debugcfg->IsEmpty())
00563             {
00564               int      type;
00565               XSTRING  aim;
00566 
00567               aim.AdjustSize(_MAXSTR);
00568               debugcfg->UnFormat(__L("%d,%s"), &type, aim.Get());
00569               aim.AdjustSize();
00570 
00571               XTRACE_SETTARGET(c, (XTRACE_TYPE)type, aim.Get());
00572 
00573               status = true;
00574             }
00575         }
00576     }
00577 
00578   return status;
00579 }
