00853 bool APPCFG::DNSResolver_GetHost(int index, XSTRING& host, XSTRING& IPresolved)
00854 {
00855   if(index < 0)                       return false;
00856   if(index >= hostsresolved.GetSize()) return false;
00857 
00858   host.AdjustSize(_MAXSTR);
00859   IPresolved.AdjustSize(_MAXSTR);
00860 
00861   hostsresolved.Get(index)->UnFormat(__L("%s,%s"), host.Get(), IPresolved.Get());
00862 
00863   host.AdjustSize();
00864   IPresolved.AdjustSize();
00865 
00866   return (host.GetSize() && IPresolved.GetSize())?true:false;
00867 }
00868 
00869 
00881 XSTRING* APPCFG::DNSResolver_GetDNSserver(int index)
00882 {
00883   if(index < 0)                       return NULL;
00884   if(index >= DNSservers.GetSize())   return NULL;
00885 
00886   if(!DNSservers.GetSize()) return NULL;
00887 
00888   return DNSservers.Get(index);
00889 }
00890 
00891 
00901 bool APPCFG::SetAutomaticDNSResolver()
00902 {
00903   for(XDWORD c=0; c<nhostsresolved; c++)
00904     {
00905       XSTRING  host;
00906       XSTRING  IPresolved;
00907 
00908       if(DNSResolver_GetHost(c, host, IPresolved))
00909         {
00910           GEN_DIODNSRESOLVER.Host_Add(host, IPresolved);
00911         }
00912     }
00913 
00914   for(XDWORD c=0; c<nDNSservers; c++)
00915     {
00916       XSTRING* dnsserver = DNSResolver_GetDNSserver(c);
00917       if(dnsserver)
00918         {
00919           GEN_DIODNSRESOLVER.DNSServer_AddDNSServer(dnsserver->Get());
00920         }
00921     }
00922 
00923   return true;
00924 }
