00374 bool APPINTERNETSERVICES::ChangeCadenceCheck(APPINTERNETSERVICES_TASKID taskID, int timecadenceseconds, bool startimmediatelycycles)
00375 {
00376   if(!xscheduler)
00377     {
00378       return false;
00379     } 
00380 
00381   if(!timecadenceseconds)    
00382     {
00383       return false;
