00385 
00386   XSCHEDULERTASK* task = NULL; 
00387 
00388   xscheduler->GetMutexScheduler()->Lock();
00389 
00390   task = xscheduler->Task_GetForID(taskID);        
00391   if(task) 
00392     {      
00393       task->SetNCycles(XSCHEDULER_CYCLEFOREVER, timecadenceseconds);
00394       task->SetIsStartImmediatelyCycles(startimmediatelycycles);
00395       task->SetIsActive(true);      
00396 
00397       task->ResetCondition();
00398     }
00399 
00400   xscheduler->GetMutexScheduler()->UnLock();       
00401 
00402   return task?true:false;
