00639 bool APPINTERNETSERVICES::CheckInternetStatus()
00640 {
00641   APPINTERNETSERVICES_XEVENT xevent(this, APPINTERNETSERVICES_XEVENT_TYPE_CHECKINTERNETCONNEXION);      
00642 
00643   xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CHECKING);  
00644   PostEvent(&xevent);
00645 
00646   haveinternetconnection = checkinternetconnection->Check();
00647 
00648   if(checkinternetconnection->IsChangeConnectionStatus())
00649     {
00650       if(haveinternetconnection)
