00652           DIOCHECKTCPIPCONNECTION_CUT*  connectioncut =  checkinternetconnection->GetLastConnectionsCut();
00653           XSTRING                       measuretime;
00654 
00655           if(connectioncut)
00656             {
00657               connectioncut->GetMeasureTimeString(measuretime, true);
00658               
00659               xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_RESTORE);
00660               xevent.SetInternetConnextionCut(connectioncut);
00661 
00662               if(cfg->InternetServices_GetCheckInternetStatusCadence())
00663                 {
00664                   // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get Internet Services cadence %d]"), cfg->InternetServices_GetCheckInternetStatusCadence());
00665 
00666                   ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_CHECKCONNECTIONINTERNET, cfg->InternetServices_GetCheckInternetStatusCadence());     
00667                 }
00668 
00669               if(cfg->InternetServices_GetCheckIPsChangeCadence())
00670                 {
00671                   // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get IPs ajust to 30 seconds]"));
00672 
00673                   ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_GETIPS, 30, false);                        
00674                 }
00675             }
00676            else
00677             {
00678               xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CHECKED); 
00679             }
00680         }
00681        else
00682         {
00683           xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CUT);
00684 
00685           if(cfg->InternetServices_GetCheckInternetStatusCadence())
00686             {
00687               // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get Internet Services cadence 10]"));    
00688 
00689               ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_CHECKCONNECTIONINTERNET, 10, false);                       
00690             }
00691         }              
00692     }
00693    else
00694     {
00695       xevent.SetInternetConnexionState(APPINTERNETSERVICES_CHECKINTERNETCONNEXION_STATE_CHECKED); 
00696     }
