00713 bool APPINTERNETSERVICES::UpdateIPs(XSTRING& actualpublicIP)
00714 {
00715   XSTRING                       actualautomaticlocalIP;
00716   DIOSTREAMIPLOCALENUMDEVICES*  enumdevices     = (DIOSTREAMIPLOCALENUMDEVICES*)GEN_DIOFACTORY.CreateStreamEnumDevices(DIOSTREAMENUMTYPE_IP_LOCAL);  
00717   bool                          sendchangeevent = false;
00718   bool                          status          = false;
00719       
00720   APPINTERNETSERVICES_XEVENT    xevent(this, APPINTERNETSERVICES_XEVENT_TYPE_CHANGEIP);  
00721 
00722   APP_LOG_ENTRY(XLOGLEVEL_INFO, APP_CFG_LOG_SECTIONID_CONNEXIONS, false, __L("[Update IPs] Ini Get Local IP... "));       
00723                                                                 
00724   if(enumdevices)
00725     {
00726       DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)enumdevices->GetFirstActiveDevice();
00727       if(device)  device->GetIP()->GetXString(actualautomaticlocalIP);
00728 
00729       alllocalIP.Empty();
00730 
00731       for(XDWORD c=0; c<enumdevices->GetDevices()->GetSize(); c++)
00732         {
00733           device = (DIOSTREAMDEVICEIP*)enumdevices->GetDevices()->Get(c);
00734           if(device)
00735             {
00736               if((!device->GetIP()->IsEmpty()) && 
00737                   (!device->GetIP()->IsAPIPA()) &&
00738                   ((device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_ETHERNET) ||
00739                    (device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_WIFI)     ||
00740                    (device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_PPP)))
00741                   {
00742                     XSTRING locaIPstring;
00743 
00744                     if(!alllocalIP.IsEmpty()) alllocalIP.Add(",");                                                                                      
00745 
00746                     device->GetIP()->GetXString(locaIPstring); 
00747                     alllocalIP.Add(locaIPstring.Get());
00748 
00749                     status = true;                                                                                      
00750                   }
00751             }  
00752         }    
00753 
00754       GEN_DIOFACTORY.DeleteStreamEnumDevices(enumdevices);
00755     }
00756 
00757   APP_LOG_ENTRY(status?XLOGLEVEL_INFO:XLOGLEVEL_ERROR, APP_CFG_LOG_SECTIONID_CONNEXIONS, false, __L("[Update IPs] End Get Local IP : [%s] "), actualautomaticlocalIP.Get());       
00758 
00759   if(status)
00760     {
00761       DIOIP ip;
00762       
00763       status = false;
00764 
00765       // Check Local IP changed
00766       if(automaticlocalIP.Compare(actualautomaticlocalIP))
00767         {                                                                      
00768           xevent.SetIsChangeLocalIP(true);          
00769           xevent.GetChangeLocalIP()->Set(actualautomaticlocalIP);
00770 
00771           automaticlocalIP  = actualautomaticlocalIP;
00772           sendchangeevent   = true;
00773         }                                                                         
00774       
00775       APP_LOG_ENTRY(XLOGLEVEL_INFO, APP_CFG_LOG_SECTIONID_CONNEXIONS, false, __L("[Update IPs] Ini Get Public IP... "));                                                                    
00776 
00777       if(scraperwebpublicIP->Get(ip, 10, NULL, false)) 
00778         {
00779           ip.GetXString(actualpublicIP);
00780         }
00781 
00782       if(!actualpublicIP.IsEmpty())
00783         {
00784           // Check Public IP changed
00785           if(publicIP.Compare(actualpublicIP))
00786             {
00787               xevent.SetIsChangePublicIP(true);          
00788               xevent.GetChangePublicIP()->Set(actualpublicIP);
00789 
00790               publicIP        = actualpublicIP;                                                                          
00791               sendchangeevent = true;                          
00792             }
00793 
00794           status = true;    
00795         }
00796 
00797       if(actualpublicIP.IsEmpty())
00798         {
00799           APP_LOG_ENTRY(XLOGLEVEL_ERROR, APP_CFG_LOG_SECTIONID_CONNEXIONS, false, __L("[Update IPs] End Get Public IP: Error not IP!"));                                                                                                                      
00800         }
00801        else
00802         {   
00803           APP_LOG_ENTRY((status?XLOGLEVEL_INFO:XLOGLEVEL_ERROR), APP_CFG_LOG_SECTIONID_CONNEXIONS, false, __L("[Update IPs] End Get Public IP: [%s] (%s)"), actualpublicIP.Get(), sendchangeevent?__L("has changed"):__L("has not changed"));                                                                                                                    
00804         }
00805     }
00806 
00807   if(sendchangeevent)
00808     {
00809       PostEvent(&xevent);                                                                      
00810     }
00811 
00812   return sendchangeevent;  
00813 }
