00889                     }
00890                     
00891                   if(cfg->InternetServices_GetUpdateTimeNTPUseDayLightSaving())
00892                     {
00893                       if(xdatetime_local->IsDayLigthSavingTime()) 
00894                         {                      
00895                           xdatetime_local->AddSeconds(3600);
00896                         }
00897                     }
00898                  
00899                   status = xdatetime_local->Write();
00900 
00901                   break;
00902                 } 
00903             }
00904 
00905           delete url;
00906         }
00907 
00908       delete ntp;
00909     } 
00910 
00911   if(status)
00912     {
00913       APPINTERNETSERVICES_XEVENT xevent(this, APPINTERNETSERVICES_XEVENT_TYPE_ADJUSTDATETIME);            
00914       PostEvent(&xevent);
