00943       case APPINTERNETSERVICES_TASKID_GETIPS                  : // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Check Get IPs..."));    
00944 
00945                                                                 if(CheckInternetConnection()) 
00946                                                                   { 
00947                                                                     XSTRING actualpublicIP;                                                                                                                             
00948 
00949                                                                     if(UpdateIPs(actualpublicIP))
00950                                                                       {  
00951                                                                         // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Update public IP"));    
00952   
00953                                                                         #ifdef XTRACE_ACTIVE                                                                   
00954                                                                         XTRACE_RESOLVEALLRESOURCES; 
00955                                                                         #endif  
00956 
00957                                                                         #ifdef APP_CFG_DYNDNSMANAGER_ACTIVE
00958                                                                         UpdateDynDNSURLs(actualpublicIP);                                                                        
00959                                                                         #endif
00960                                                                       }   
00961 
00962                                                                     if(!actualpublicIP.IsEmpty())
00963                                                                       {
00964                                                                         #ifdef APP_CFG_DYNDNSMANAGER_ACTIVE                                                                  
00965                                                                         // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[APP Internet Services] Get IPs ajust to %d seconds]"), cfg->InternetServices_GetCheckIPsChangeCadence());
00966                                                                         ChangeCadenceCheck(APPINTERNETSERVICES_TASKID_GETIPS, cfg->InternetServices_GetCheckIPsChangeCadence());   
00967                                                                         #endif                                                                                                                                           
00968                                                                       }
00969                                                                   }
00970                                                                 break;
00971 
00972       case APPINTERNETSERVICES_TASKID_CHECKNTPDATETIME        : if(CheckInternetConnection()) 
00973                                                                  {
00974                                                                    AdjustTimerByNTP(&NTPservers);                                                                  
00975                                                                  }
00976                                                                 break;
00977     }
00978 
00979   
00980   if(checkinternetconnection)
00981     {
00982       checkinternetconnection->GetCheckConnections()->Connections_GetMutex()->UnLock();
00983     }
00984 
00985 }
