00998 void APPINTERNETSERVICES::HandleEvent(XEVENT* xevent)
00999 {
01000   if(!xevent) return;
01001 
01002   switch(xevent->GetEventFamily())
01003     {
01004       case XEVENT_TYPE_SCHEDULER       : {  XSCHEDULER_XEVENT* event = (XSCHEDULER_XEVENT*)xevent;
01005                                             if(!event) return;
01006 
01007                                             HandleEvent_Scheduler(event);
01008                                          }
01009                                          break;
01010     }
01011 }
