00425 bool APPUPDATE::EventManager(APPUPDATE_EVENTOPERATION eventoperation, XSUBJECT* xsubject, XOBSERVER* observer)
00426 {
00427   XDWORD events[] = {  DIOAPPLICATIONUPDATE_XEVENT_TYPE_GETVERSIONAVAILABLE                                    ,
00428                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_CHECKVERSIONAVAILABLE                                    ,
00429                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILE                                           ,
00430                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UNZIPFILE                                              ,
00431                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILES                                          ,
00432                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILE                                     ,
00433                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILES                                    ,
00434                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_RELEASERESOURCES                                   ,
00435                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILE                                             ,
00436                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEEXECFILE                                         ,
00437                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILES                                            ,
00438                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_END                                                ,
00439                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILE                                      ,
00440                        DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILES                                     ,
00441                     };
00442 
00443 
00444   for(XDWORD c=0; c< sizeof(events)/sizeof(XDWORD); c++)
00445     {
00446       switch(eventoperation)
00447         {
00448           case APPUPDATE_EVENTOPERATION_REGISTER     : RegisterEvent(events[c]);                           break;
00449           case APPUPDATE_EVENTOPERATION_DEREGISTER   : DeRegisterEvent(events[c]);                         break;
00450           case APPUPDATE_EVENTOPERATION_SUBSCRIBE    : SubscribeEvent(events[c]   , xsubject, observer);   break;
00451           case APPUPDATE_EVENTOPERATION_UNSUBSCRIBE  : UnSubscribeEvent(events[c] , xsubject, observer);   break;
00452                                            default   : return false;
00453         }
00454     }
00455 
00456   return true;
00457 }
