00756 bool APPUPDATE::LogOfEvent(DIOAPPLICATIONUPDATE_XEVENT* event)
00757 {
00758   DIOAPPLICATIONUPDATE_FILE*  updatefile = event->GetActualFileToUpdate();
00759   XSTRING                     string;
00760 
00761   switch(event->GetEventType())
00762     {
00763       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_GETVERSIONAVAILABLE       : if(event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR)
00764                                                                           {
00765                                                                             string.Format( __L("Update Version available %d.%d.%d")
00766                                                                                           , event->GetVersionData()->GetVersion()
00767                                                                                           , event->GetVersionData()->GetSubVersion()
00768                                                                                           , event->GetVersionData()->GetSubVersionError());
00769                                                                           }
00770                                                                          else
00771                                                                           {
00772                                                                             string.Format( __L("No version available to update"));
00773                                                                           }
00774                                                                         break;
00775 
00776       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_CHECKVERSIONAVAILABLE     : string.Format( __L("Version %d.%d.%d is ")
00777                                                                                       , event->GetVersionData()->GetVersion()
00778                                                                                       , event->GetVersionData()->GetSubVersion()
00779                                                                                       , event->GetVersionData()->GetSubVersionError());
00780 
00781                                                                         switch(event->GetVersionComparation())
00782                                                                           {
00783                                                                             case -1 : string.Add(__L("old"));   break;
00784                                                                             case  0 : string.Add(__L("equal"));  break;
00785                                                                             case  1 : string.Add(__L("new"));    break;
00786                                                                           }
00787 
00788                                                                         if(event->GetVersionComparation() <=0) string.AddFormat(__L(". It will not be updated"));
00789 
00790                                                                         break;
00791 
00792 
00793       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILE              : if(updatefile) string.Format( __L("Download file %s (%dk).")
00794                                                                                                       , updatefile->GetXPathFile()->Get()
00795                                                                                                       , updatefile->GetSize()/1024);
00796 
00797                                                                         break;
00798 
00799       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UNZIPFILE                 : if(updatefile) string.Format( __L("Unzip file %s (%dk) -> %s.")
00800                                                                                                       , updatefile->GetXPathFile()->Get()
00801                                                                                                       , updatefile->GetSize()/1024
00802                                                                                                       , event->GetActualUnzipFileName()->Get());
00803                                                                         break;
00804 
00805       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_DOWNLOADFILES             : string.Format(__L("Downloads file %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00806                                                                         break;
00807 
00808       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILE        : if(updatefile) string.Format( __L("Backup original file %s (%dk).")
00809                                                                                                       , updatefile->GetXPathFile()->Get()
00810                                                                                                       , updatefile->GetSize()/1024);
00811                                                                         break;
00812 
00813       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_BACKUPORIGINALFILES       : string.Format(__L("Backup Original file %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00814                                                                         break;
00815 
00816       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_RELEASERESOURCES      : string.Format(__L("Application release blocked resources"));
00817                                                                         break;
00818 
00819 
00820       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILE                : if(updatefile) string.Format( __L("Copy update file %s (%dk).")
00821                                                                                                       , updatefile->GetXPathFile()->Get()
00822                                                                                                       , updatefile->GetSize()/1024);
00823                                                                         break;
00824 
00825        case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEEXECFILE           : if(updatefile) string.Format( __L("Notify update EXEC file %s (%dk).")
00826                                                                                                       , updatefile->GetXPathFile()->Get()
00827                                                                                                       , updatefile->GetSize()/1024);
00828                                                                         break;
00829 
00830       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILES               : string.Format(__L("Update files %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00831                                                                         break;
00832 
00833 
00834       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_APP_END                   : string.Format(__L("Terminate application"));
00835                                                                         break;
00836 
00837       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILE         : if(updatefile) string.Format( __L("Restore file %s from backup (%dk).")
00838                                                                                                     , updatefile->GetXPathFile()->Get()
00839                                                                                                     , updatefile->GetSize()/1024);
00840                                                                         break;
00841 
00842       case DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILES        : string.Format(__L("Restore update files %s."), (event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR?__L("Ok."):__L("ERROR!")));
00843                                                                         break;
00844     }
00845 
00846   if(string.GetSize())
00847     {
00848       XLOG::GetInstance().AddEntry((event->GetError() == DIOAPPLICATIONUPDATE_NOT_ERROR)?XLOGLEVEL_INFO:XLOGLEVEL_ERROR, DIOAPPLICATIONUPDATE_LOG_SECTIONID_APPUPDATE, false, string.Get());
