00896 bool APPUPDATE::CreateTaskUpdate(bool doajust)
00897 {
00898   if(!app_cfg) return false;
00899 
00900   XSCHEDULERTASK* xtask = new XSCHEDULERTASK(xscheduler);
00901   if(!xtask) return false;
00902 
00903   bool toactive = false;
00904 
00905   XDATETIME* GEN_XFACTORY_CREATE(start, CreateDateTime())
00906   XDATETIME* GEN_XFACTORY_CREATE(end, CreateDateTime())
00907   if(start && end)
00908     {
00909       if(!app_cfg->ApplicationUpdate_GetCheckTime()->IsEmpty())
00910         {
00911           start->Read();
00912 
00913           start->GetDateTimeFromString((*app_cfg->ApplicationUpdate_GetCheckTime()), XDATETIME_FORMAT_ADDTIME);
00914           start->SetSeconds(0);
00915           start->SetMilliSeconds(0);
00916 
00917           start->AddDays(1);
00918 
00919           end->Set(start);
00920           end->AddSeconds(5);
00921 
00922           xtask->SetTimeLimits(start, end);
00923 
00924           toactive = true;
00925         }
00926        else
00927         {
00928           int minutescadence = app_cfg->ApplicationUpdate_GetCheckCadence();
00929           if(minutescadence)
00930             {
00931               start->Read();
00932 
00933               if(doajust)
00934                 {
00935                   int minutes = ((start->GetMinutes() / minutescadence) + ((start->GetMinutes() % minutescadence)?1:0)) * minutescadence;
00936                   if(minutes == start->GetMinutes()) minutes += minutescadence;
00937                   if(minutes >= 60)
00938                     {
00939                       start->AddHours(1);
00940                       minutes = 0;
00941                     }
00942 
00943                   start->SetMinutes(minutes);
00944 
00945                 } else start->AddMinutes(minutescadence);
00946 
00947               start->SetSeconds(0);
00948               start->SetMilliSeconds(0);
00949 
00950               xtask->SetTimeLimits(start);
00951 
00952               toactive = true;
00953             }
00954         }
00955     }
00956 
00957   if(toactive)
00958     {
00959       xtask->SetID(APPUPDATE_TASKID_CHECKUPDATEAPPLICATION);
00960       xtask->SetNCycles(1);
00961       xtask->SetIsActive(true);
00962 
00963       xscheduler->Task_Add(xtask);
00964     }
00965    else delete xtask;
00966 
00967   GEN_XFACTORY.DeleteDateTime(start);
00968   GEN_XFACTORY.DeleteDateTime(end);
00969 
00970   return true;
00971 }
