01020 void APPUPDATE::HandleEvent(XEVENT* xevent)
01021 {
01022   if(!xevent) return;
01023 
01024   switch(xevent->GetEventFamily())
01025     {
01026       case XEVENT_TYPE_SCHEDULER           : { XSCHEDULER_XEVENT* event = (XSCHEDULER_XEVENT*)xevent;
01027                                               if(!event) return;
01028 
01029                                               HandleEvent_Scheduler(event);
01030                                             }
01031                                             break;
01032 
01033       case XEVENT_TYPE_APPLICATIONUPDATE   : { DIOAPPLICATIONUPDATE_XEVENT* event = (DIOAPPLICATIONUPDATE_XEVENT*)xevent;
01034                                               if(!event) return;
01035 
01036                                               HandleEvent_ApplicationUpdate(event);
01037                                             }
01038                                             break;
01039     }
01040 
01041 }
