00104 bool CIPHER3DES::Cipher(XBYTE* input, XDWORD size)
00105 {
00106   if(!size) return false;
00107 
00108   XBUFFER             inputpadding;
00109   CIPHER3DES_CONTEXT  ctx;
00110   XBYTE               iv[8];
00111 
00112   memset(iv,0,8);
00113   if(inivector && inivector->GetSize()>=8) memcpy(iv, inivector->Get() ,8);
00114 
00115   inputpadding.Add(input, size);
00116   inputpadding.Padding_Add(paddingadjustsize, paddingtype);
00117 
00118   result->Delete();
00119   result->Add(input, size);
00120   result->Padding_Add(paddingadjustsize, paddingtype);
00121 
00122   CIPHERKEYSYMMETRICAL* key = (CIPHERKEYSYMMETRICAL*)GetKey();
00123   if(!key)               return false;
00124   if(!key->Get()->Get()) return false;
00125 
00126   if(key->Get()->GetSize() <= 16)
00127         DES3Set2KeyCipher(&ctx, (XBYTE *)key->Get()->Get());
