00129 
00130   bool status;
00131 
00132   if(this->GetChainingMode() == CIPHERCHAININGMODE_ECB)
00133          status = DES3Cipher_ECB(&ctx, CIPHERDES_ENCRYPT, result->GetSize(), iv, inputpadding.Get(),  result->Get());
00134     else status = DES3Cipher_CBC(&ctx, CIPHERDES_ENCRYPT, result->GetSize(), iv, inputpadding.Get(),  result->Get());
