00408 void CIPHERCURVE25519::freduce_degree(XQWORDSIG* output) 
00409 {
00410   // Each of these shifts and adds ends up multiplying the value by 19.
00411   output[8] += output[18] << 4;
00412   output[8] += output[18] << 1;
00413   output[8] += output[18];
00414   output[7] += output[17] << 4;
00415   output[7] += output[17] << 1;
00416   output[7] += output[17];
00417   output[6] += output[16] << 4;
00418   output[6] += output[16] << 1;
00419   output[6] += output[16];
00420   output[5] += output[15] << 4;
00421   output[5] += output[15] << 1;
00422   output[5] += output[15];
00423   output[4] += output[14] << 4;
00424   output[4] += output[14] << 1;
00425   output[4] += output[14];
00426   output[3] += output[13] << 4;
00427   output[3] += output[13] << 1;
00428   output[3] += output[13];
00429   output[2] += output[12] << 4;
00430   output[2] += output[12] << 1;
00431   output[2] += output[12];
00432   output[1] += output[11] << 4;
00433   output[1] += output[11] << 1;
00434   output[1] += output[11];
00435   output[0] += output[10] << 4;
00436   output[0] += output[10] << 1;
00437   output[0] += output[10];
00438 }
