00452 XQWORDSIG CIPHERCURVE25519::div_by_2_26(const XQWORDSIG v) 
00453 {  
00454   const XDWORD highword     = (XDWORD)(((XQWORD) v) >> 32);   // High word of v; no shift needed
00455   const XDWORDSIG sign      = ((XDWORDSIG) highword) >> 31;   // Set to all 1s if v was negative; else set to 0s.   
00456   const XDWORDSIG roundoff  = ((XDWORD) sign) >> 6;           // Set to 0x3ffffff if v was negative; else set to 0. 
00457   
00458   return (v + roundoff) >> 26;    // Should return v / (1<<26) 
00459 }
00460 
00461 
00473 XQWORDSIG CIPHERCURVE25519::div_by_2_25(const XQWORDSIG v) 
00474 {  
00475   const XDWORD    highword  = (XDWORD)(((XQWORD) v) >> 32);   // High word of v; no shift needed  
00476   const XDWORDSIG sign      = ((XDWORDSIG) highword) >> 31;   // Set to all 1s if v was negative; else set to 0s.   
00477   const XDWORDSIG roundoff  = ((XDWORD) sign) >> 7;           // Set to 0x1ffffff if v was negative; else set to 0. 
00478  
00479   return (v + roundoff) >> 25; // Should return v / (1<<25) 
00480 }
