00494 XDWORDSIG CIPHERCURVE25519::div_s32_by_2_25(const XDWORDSIG v) 
00495 {
00496    const XDWORDSIG roundoff = ((XDWORD)(v >> 31)) >> 7;
00497 
00498    return (v + roundoff) >> 25;
00499 }
