00512 void CIPHERCURVE25519::freduce_coefficients(XQWORDSIG* output) 
00513 {
00514   XDWORD i;
00515 
00516   output[10] = 0;
00517 
00518   for(i=0; i<10; i+=2) 
00519     {
00520       XQWORDSIG over = div_by_2_26(output[i]);
00521 
00522       output[i] -= over << 26;
00523       output[i+1] += over;
00524 
00525       over = div_by_2_25(output[i+1]);
00526       output[i+1] -= over << 25;
00527       output[i+2] += over;
00528     }
00529 
00530   // Now |output[10]| < 2 ^ 38 and all other coefficients are reduced. 
00531   output[0] += output[10] << 4;
00532   output[0] += output[10] << 1;
00533   output[0] += output[10];
00534 
00535   output[10] = 0;
00536 
00537   // Now output[1..9] are reduced, and |output[0]| < 2^26 + 19 * 2^38  So |over| will be no more than 77825  
00538   {
00539     XQWORDSIG over = div_by_2_26(output[0]);
00540     output[0] -= over << 26;
00541     output[1] += over;
00542   }
00543 
00544   // Now output[0,2..9] are reduced, and |output[1]| < 2^25 + 77825  So |over| will be no more than 1. 
00545   {
00546     // output[1] fits in 32 bits, so we can use div_s32_by_2_25 here. 
00547     XDWORDSIG over32 = div_s32_by_2_25((XDWORDSIG)output[1]);
00548     output[1] -= over32 << 25;
00549     output[2] += over32;
00550   }
00551 
00552   // Finally, output[0,1,3..9] are reduced, and output[2] is "nearly reduced": 
00553   // we have |output[2]| <= 2^26.  This is good enough for all of our math, but it will require an extra freduce_coefficients before fcontract.
00554 }
