00569 void CIPHERCURVE25519::fmul(XQWORDSIG* output, const XQWORDSIG* in, const XQWORDSIG* in2) 
00570 {
00571   XQWORDSIG t[19];
00572 
00573   fproduct(t, in, in2);
00574   freduce_degree(t);
00575   freduce_coefficients(t);
00576 
00577   memcpy(output, t, sizeof(XQWORDSIG) * 10);
00578 }
