00615                     ((XQWORDSIG) ((XDWORDSIG) in[0])) * ((XDWORDSIG) in[8]) +
00616                2 * (((XQWORDSIG) ((XDWORDSIG) in[1])) * ((XDWORDSIG) in[7]) +
00617                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[5])));
00618   output[9] =  2 * (((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[5]) +
00619                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[6]) +
00620                     ((XQWORDSIG) ((XDWORDSIG) in[2])) * ((XDWORDSIG) in[7]) +
00621                     ((XQWORDSIG) ((XDWORDSIG) in[1])) * ((XDWORDSIG) in[8]) +
00622                     ((XQWORDSIG) ((XDWORDSIG) in[0])) * ((XDWORDSIG) in[9]));
00623   output[10] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[5]) +
00624                     ((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[6]) +
00625                     ((XQWORDSIG) ((XDWORDSIG) in[2])) * ((XDWORDSIG) in[8]) +
00626                2 * (((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[7]) +
00627                     ((XQWORDSIG) ((XDWORDSIG) in[1])) * ((XDWORDSIG) in[9])));
00628   output[11] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[6]) +
00629                     ((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[7]) +
00630                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[8]) +
00631                     ((XQWORDSIG) ((XDWORDSIG) in[2])) * ((XDWORDSIG) in[9]));
00632   output[12] =      ((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[6]) +
00633                2 * (((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[8]) +
00634                2 * (((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[7]) +
00635                     ((XQWORDSIG) ((XDWORDSIG) in[3])) * ((XDWORDSIG) in[9])));
00636   output[13] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[7]) +
00637                     ((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[8]) +
00638                     ((XQWORDSIG) ((XDWORDSIG) in[4])) * ((XDWORDSIG) in[9]));
00639   output[14] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[7])) * ((XDWORDSIG) in[7]) +
00640                     ((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[8]) +
00641                2 *  ((XQWORDSIG) ((XDWORDSIG) in[5])) * ((XDWORDSIG) in[9]));
00642   output[15] = 2 * (((XQWORDSIG) ((XDWORDSIG) in[7])) * ((XDWORDSIG) in[8]) +
00643                     ((XQWORDSIG) ((XDWORDSIG) in[6])) * ((XDWORDSIG) in[9]));
00644   output[16] =      ((XQWORDSIG) ((XDWORDSIG) in[8])) * ((XDWORDSIG) in[8]) +
00645                4 *  ((XQWORDSIG) ((XDWORDSIG) in[7])) * ((XDWORDSIG) in[9]);
00646   output[17] = 2 *  ((XQWORDSIG) ((XDWORDSIG) in[8])) * ((XDWORDSIG) in[9]);
00647   output[18] = 2 *  ((XQWORDSIG) ((XDWORDSIG) in[9])) * ((XDWORDSIG) in[9]);
00648 }
