00803 void CIPHERCURVE25519::fmonty(XQWORDSIG* x2, XQWORDSIG* z2, XQWORDSIG* x3, XQWORDSIG* z3, XQWORDSIG* x, XQWORDSIG* z, XQWORDSIG* xprime, XQWORDSIG* zprime, const XQWORDSIG* qmqp) 
00804 {
00805   XQWORDSIG origx[10];
00806   XQWORDSIG origxprime[10];
00807   XQWORDSIG zzz[19];
00808   XQWORDSIG xx[19]; 
00809   XQWORDSIG zz[19];
00810   XQWORDSIG xxprime[19];
00811   XQWORDSIG zzprime[19];
00812   XQWORDSIG zzzprime[19]; 
00813   XQWORDSIG xxxprime[19];
00814 
00815   memcpy(origx, x, 10 * sizeof(XQWORDSIG));
00816   fsum(x, z);
00817   fdifference(z, origx);                          // does x - z
00818 
00819   memcpy(origxprime, xprime, sizeof(XQWORDSIG) * 10);
00820   fsum(xprime, zprime);
00821   fdifference(zprime, origxprime);
00822   fproduct(xxprime, xprime, z);
00823   fproduct(zzprime, x, zprime);
00824   freduce_degree(xxprime);
00825   freduce_coefficients(xxprime);
00826   freduce_degree(zzprime);
00827   freduce_coefficients(zzprime);
00828   memcpy(origxprime, xxprime, sizeof(XQWORDSIG) * 10);
00829   fsum(xxprime, zzprime);
00830   fdifference(zzprime, origxprime);
00831   fsquare(xxxprime, xxprime);
00832   fsquare(zzzprime, zzprime);
00833   fproduct(zzprime, zzzprime, qmqp);
00834   freduce_degree(zzprime);
00835   freduce_coefficients(zzprime);
00836   memcpy(x3, xxxprime, sizeof(XQWORDSIG) * 10);
00837   memcpy(z3, zzprime, sizeof(XQWORDSIG) * 10);
00838 
00839   fsquare(xx, x);
00840   fsquare(zz, z);
00841   fproduct(x2, xx, zz);
00842   freduce_degree(x2);
00843   freduce_coefficients(x2);
00844   fdifference(zz, xx);                            // does zz = xx - zz
00845   memset(zzz + 10, 0, sizeof(XQWORDSIG) * 9);
00846   fscalar_product(zzz, zz, 121665);
00847 
00848   // No need to call freduce_degree here: fscalar_product doesn't increase the degree of its input. 
00849   freduce_coefficients(zzz);
00850   fsum(zzz, xx);
00851   fproduct(z2, zz, zzz);
00852   freduce_degree(z2);
00853   freduce_coefficients(z2);
00854 }
