00875 void CIPHERCURVE25519::swap_conditional(XQWORDSIG a[19], XQWORDSIG b[19], XQWORDSIG iswap) 
00876 {
00877   const XDWORDSIG swap = (XDWORDSIG)-iswap;  
00878   XDWORD i;
00879 
00880   for(i=0; i<10; ++i) 
00881     {
00882       const XDWORDSIG x = swap & ( ((XDWORDSIG)a[i]) ^ ((XDWORDSIG)b[i]) );
00883 
00884       a[i] = ((XDWORDSIG)a[i]) ^ x;
00885       b[i] = ((XDWORDSIG)b[i]) ^ x;
00886     }
00887 }
