00905 void CIPHERCURVE25519::cmult(XQWORDSIG* resultx, XQWORDSIG* resultz, const XBYTE* n, const XQWORDSIG* q) 
00906 {
00907   XQWORDSIG   a[19]   = {0};  
00908   XQWORDSIG   b[19]   = {1};  
00909   XQWORDSIG   c[19]   = {1};  
00910   XQWORDSIG   d[19]   = {0};
00911   XQWORDSIG*  nqpqx   = a;
00912   XQWORDSIG*  nqpqz   = b; 
00913   XQWORDSIG*  nqx     = c;
00914   XQWORDSIG*  nqz     = d;
00915   XQWORDSIG*  t;
00916   XQWORDSIG   e[19]   = {0}; 
00917   XQWORDSIG   f[19]   = {1}; 
00918   XQWORDSIG   g[19]   = {0}; 
00919   XQWORDSIG   h[19]   = {1};
00920   XQWORDSIG*  nqpqx2  = e;
00921   XQWORDSIG*  nqpqz2  = f;
00922   XQWORDSIG*  nqx2    = g;
00923   XQWORDSIG*  nqz2    = h;
00924   XDWORD    i;
00925   XDWORD    j;
00926 
00927   memcpy(nqpqx, q, sizeof(XQWORDSIG) * 10);
00928 
00929   for(i=0; i<32; ++i) 
00930     {
00931       XBYTE byte = n[31 - i];
00932       for(j=0; j<8; ++j) 
00933         {
00934           const XQWORDSIG bit = byte >> 7;
00935 
00936           swap_conditional(nqx, nqpqx, bit);
00937           swap_conditional(nqz, nqpqz, bit);
00938           fmonty(nqx2, nqz2, nqpqx2, nqpqz2, nqx, nqz, nqpqx, nqpqz, q);
00939           swap_conditional(nqx2, nqpqx2, bit);
00940           swap_conditional(nqz2, nqpqz2, bit);
00941 
00942           t       = nqx;
00943           nqx     = nqx2;
00944           nqx2    = t;
00945           t       = nqz;
00946           nqz     = nqz2;
00947           nqz2    = t;
00948           t       = nqpqx;
00949           nqpqx   = nqpqx2;
00950           nqpqx2  = t;
00951           t       = nqpqz;
00952           nqpqz   = nqpqz2;
00953           nqpqz2  = t;
00954 
00955           byte  <<= 1;
00956         }
00957     }
00958 
00959   memcpy(resultx, nqx, sizeof(XQWORDSIG) * 10);
00960   memcpy(resultz, nqz, sizeof(XQWORDSIG) * 10);
00961 }
