00963 
00974 void CIPHERCURVE25519::crecip(XQWORDSIG* out, const XQWORDSIG* z) 
00975 {
00976   XQWORDSIG z2[10];
00977   XQWORDSIG z9[10];
00978   XQWORDSIG z11[10];
00979   XQWORDSIG z2_5_0[10];
00980   XQWORDSIG z2_10_0[10];
00981   XQWORDSIG z2_20_0[10];
00982   XQWORDSIG z2_50_0[10];
00983   XQWORDSIG z2_100_0[10];
00984   XQWORDSIG t0[10];
00985   XQWORDSIG t1[10];
00986   int       i;
00987 
00988   fsquare(z2,z);                    // 2 
00989   fsquare(t1,z2);                   // 4 
00990   fsquare(t0,t1);                   // 8 
00991   fmul(z9,t0,z);                    // 9 
00992   fmul(z11,z9,z2);                  // 11 
00993   fsquare(t0,z11);                  // 22  
00994   fmul(z2_5_0,t0,z9);               // 2^5 - 2^0 = 31 
00995 
00996   fsquare(t0,z2_5_0);               // 2^6 - 2^1  
00997   fsquare(t1,t0);                   // 2^7 - 2^2  
00998   fsquare(t0,t1);                   // 2^8 - 2^3 
00999   fsquare(t1,t0);                   // 2^9 - 2^4  
01000   fsquare(t0,t1);                   // 2^10 - 2^5 
01001   fmul(z2_10_0,t0,z2_5_0);          // 2^10 - 2^0 
01002 
01003   fsquare(t0,z2_10_0);              // 2^11 - 2^1 
01004   fsquare(t1,t0);                   // 2^12 - 2^2 
01005   for(i=2; i<10; i+=2)              // 2^20 - 2^10   
01006     { 
01007       fsquare(t0,t1); fsquare(t1,t0); 
01008     }
01009   fmul(z2_20_0,t1,z2_10_0);         // 2^20 - 2^0 
01010 
01011   fsquare(t0,z2_20_0);              // 2^21 - 2^1 
01012   fsquare(t1,t0);                   // 2^22 - 2^2 
01013   for (i=2; i<20; i+=2)             // 2^40 - 2^20 
01014     { fsquare(t0,t1); fsquare(t1,t0); 
01015     }
01016   fmul(t0,t1,z2_20_0);              // 2^40 - 2^0 
01017 
01018   fsquare(t1,t0);                   // 2^41 - 2^1 
01019   fsquare(t0,t1);                   // 2^42 - 2^2  
01020   for(i=2; i<10; i+=2)              // 2^50 - 2^10  
01021   { 
01022     fsquare(t1,t0); fsquare(t0,t1); 
01023   }
01024   fmul(z2_50_0,t0,z2_10_0);         // 2^50 - 2^0 
01025 
01026   fsquare(t0,z2_50_0);              // 2^51 - 2^1 
01027   fsquare(t1,t0);                   // 2^52 - 2^2 
01028   for(i=2; i<50; i+=2)              // 2^100 - 2^50  
01029     { 
01030       fsquare(t0,t1); fsquare(t1,t0); 
01031     }
01032   fmul(z2_100_0,t1,z2_50_0);        // 2^100 - 2^0  
01033 
01034   fsquare(t1,z2_100_0);             // 2^101 - 2^1 
01035   fsquare(t0,t1);                   // 2^102 - 2^2 
01036   for(i=2; i<100; i+=2)             // 2^200 - 2^100   
01037     { 
01038       fsquare(t1,t0); fsquare(t0,t1); 
01039     }
01040   fmul(t1,t0,z2_100_0);             // 2^200 - 2^0 
01041 
01042   fsquare(t0,t1);                   // 2^201 - 2^1 
01043   fsquare(t1,t0);                   // 2^202 - 2^2 
01044   for(i=2; i<50; i+=2)              // 2^250 - 2^50 
01045     { 
01046       fsquare(t0,t1); fsquare(t1,t0); 
01047     }
01048   fmul(t0,t1,z2_50_0);              // 2^250 - 2^0 
01049 
01050   fsquare(t1,t0);                   // 2^251 - 2^1 
01051   fsquare(t0,t1);                   // 2^252 - 2^2 
01052   fsquare(t1,t0);                   // 2^253 - 2^3 
01053   fsquare(t0,t1);                   // 2^254 - 2^4 
01054   fsquare(t1,t0);                   // 2^255 - 2^5 
01055   fmul(out,t1,z11);                 // 2^255 - 21 
01056 }
