01058 
01070 void CIPHERCURVE25519::curve25519(XBYTE* mypublic, const XBYTE* secret, const XBYTE* basepoint) 
01071 {
01072   XQWORDSIG bp[10];
01073   XQWORDSIG x[10];
01074   XQWORDSIG z[11]; 
01075   XQWORDSIG zmone[10];
01076   XBYTE     e[CIPHERCURVE25519_MAXKEY];
01077   int       i;
01078 
01079   if(basepoint == 0) 
01080     {
01081       basepoint = CIPHERCURVE25519::basepoint;
01082     }
01083 
01084   for(i = 0; i < CIPHERCURVE25519_MAXKEY; ++i) 
01085     { 
01086       e[i] = secret[i];
01087     }
01088 
01089   e[0]  &= 248;
01090   e[31] &= 127;
01091   e[31] |= 64;
01092 
01093   fexpand(bp, basepoint);
01094   cmult(x, z, e, bp);
01095   crecip(zmone, z);
01096   fmul(z, x, zmone);
01097   freduce_coefficients(z);
01098   fcontract(mypublic, z);
01099 }
