00480 bool DIOAIOBIMAINBOARD::SetDigitalOutput(XBYTE bit, bool on)
00481 {
00482   if(!pcf8574[DIOAIOBIMAINBOARD_I2CDIGIGPIO_OUT])                           return false;
00483   if(!pcf8574[DIOAIOBIMAINBOARD_I2CDIGIGPIO_OUT]->IsInitialized())          return false;
00484 
00485   XBYTE mask = 0x01;
00486 
00487   if(bit-1)
00488     {
00489       mask <<= (bit-1);
00490     }
00491 
00492   if(on)
00493         outputdigitaldata |=  mask;
00494    else outputdigitaldata &= ~mask;
00495 
00496   return pcf8574[DIOAIOBIMAINBOARD_I2CDIGIGPIO_OUT]->Write(outputdigitaldata);
00497 }
