00644 bool DIOAIOBIMAINBOARD::ResetMicroControler()
00645 {
00646   if(!SetModeHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT,  9, false)) return false;   // Reset Micro
00647   if(!SetStatusHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT, 9, true)) return false;
00648 
00649   bool pinstatus = false;
00650 
00651   if(SetStatusHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT, 9,  pinstatus))
00652     {
00653       GEN_XSLEEP.MilliSeconds(10);
00654 
00655       pinstatus = !pinstatus;
00656 
00657       return SetStatusHeaderPin(DIOAIOBIMAINBOARD_HEADER_SLOT, 9,  pinstatus);
00658     }
00659 
00660   return false;
00661 }
