00673 bool DIOAIOBIMAINBOARD::End()
00674 {
00675   for(int c=0; c<3; c++)
00676     {
00677       if(pcf8574[c])
00678         {
00679           pcf8574[c]->End();
00680 
00681           delete pcf8574[c];
00682           pcf8574[c] = NULL;
00683         }
00684     }
00685 
00686   if(eeprom)
00687     {
00688       eeprom->End();
00689       delete eeprom;
00690       eeprom = NULL;
00691     }
00692 
00693 
00694   if(pca9685)
00695     {
00696       pca9685->GetDIOStream()->WaitToFlushXBuffers(3);
00697       pca9685->End();
00698       delete pca9685;
00699       pca9685 = NULL;
00700     }
00701 
00702   if(gpio)
00703     {
00704       SetLedA(false);
00705       SetLedB(false);
00706 
00707       gpio->End();
00708       GEN_DIOFACTORY.DeleteGPIO(gpio);
00709       gpio = NULL;
00710     }
00711 
00712   return true;
00713 }
