00236 int DIOANDROIDSTREAMBLUETOOTH::IsReadyConnect(int socket)
00237 {
00238   struct timeval  tv;
00239   int             rc;
00240   fd_set          fdr;
00241   fd_set          fdw;
00242   fd_set          fds;
00243 
00244   if(socket==-1) return -1;
00245 
00246   FD_ZERO(&fdr);
00247   FD_ZERO(&fdw);
00248   FD_ZERO(&fds);
00249 
00250   FD_SET((unsigned int)socket,&fdr);
00251   FD_SET((unsigned int)socket,&fdw);
00252   FD_SET((unsigned int)socket,&fds);
00253 
00254   tv.tv_sec  = 0;
00255   tv.tv_usec = 100;
00256 
00257   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00258   if(rc==-1) return -1;
00259   if(rc== 2) return -1;
00260 
00261   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00262   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00263   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00264 
00265   if(status3) return -1;
00266   if(((status1) || (status2)) && (rc==1))  return  1;
00267 
00268   return 0;
00269 }
