00705                                                                     diostream->SetEvent(DIOANDROIDBTFSMEVENT_DISCONNECTING);
00706                                                                     break;
00707                                                                   }
00708 
00709                                                                 rem_addr.rc_family = AF_BLUETOOTH;
00710                                                                 rem_addr.rc_channel = (uint8_t) diostream->config->GetRemoteChannel();
00711 
00712                                                                 { XBUFFER charstr;
00713                                                                   
00714                                                                   remoteMACstring.ConvertToASCII(charstr);                                                                  
00715                                                                   str2ba(charstr.GetPtrChar(), &rem_addr.rc_bdaddr);                                                                  
00716                                                                 }
00717 
00718                                                                 diostream->SetIsRefusedConnection(false);
00719 
00720                                                                 fcntl(diostream->handlesocket, F_SETFL, fcntl(diostream->handlesocket, F_GETFL,0) | O_NONBLOCK);
00721 
00722                                                                 connect(diostream->handlesocket, (struct sockaddr *)&rem_addr, sizeof(rem_addr));
00723 
00724                                                               }
00725                                                           }
00726                                                           break;
00727 
00728               case DIOANDROIDBTFSMSTATE_CONNECTED         : diostream->SetEvent(DIOANDROIDBTFSMEVENT_WAITINGTOREAD);
00729                                                           break;
00730 
00731               case DIOANDROIDBTFSMSTATE_WAITINGTOREAD     : break;
00732 
00733               case DIOANDROIDBTFSMSTATE_SENDINGDATA       : break;
00734 
00735               case DIOANDROIDBTFSMSTATE_DISCONNECTING     : if(diostream->config->GetPIN()->GetSize())
00736                                                             {
00737                                                               XSTRING localMACstring;
00738                                                               XSTRING remoteMACstring;
00739 
00740                                                               diostream->config->GetLocalMAC()->GetXString(localMACstring);
00741                                                               diostream->config->GetRemoteMAC()->GetXString(remoteMACstring);
00742 
00743                                                               diostream->ManagementOfPIN(false, localMACstring, remoteMACstring, (*diostream->config->GetPIN()));
00744                                                             }
00745 
00746                                                           diostream->threadconnection->Run(false);
00747                                                           diostream->status = DIOSTREAMSTATUS_DISCONNECTED;
00748                                                           break;
00749             }
00750         }
00751     }
00752 }
00753 
00754 
00763 void DIOANDROIDSTREAMBLUETOOTH::Clean()
00764 {
00765   threadconnection   = NULL;
00766   status            = DIOSTREAMSTATUS_DISCONNECTED;
00767 
00768   handlesocket      = -1;
00769   sdpserversession  = NULL;
00770   handleserver      = -1;
00771 
00772   memset(buffer,0,DIOSTREAM_MAXBUFFER);
00773 }
