00289 int DIOANDROIDSTREAMBLUETOOTHREMOTEENUMDEVICES::IsReadyConnect(int socket)
00290 {
00291   struct timeval  tv;
00292   int             rc;
00293   fd_set          fdr;
00294   fd_set          fdw;
00295   fd_set          fds;
00296 
00297   if(socket==-1) return -1;
00298 
00299   FD_ZERO(&fdr);
00300   FD_ZERO(&fdw);
00301   FD_ZERO(&fds);
00302 
00303   FD_SET((unsigned int)socket,&fdr);
