00305   FD_SET((unsigned int)socket,&fds);
00306 
00307   tv.tv_sec  = 0;
00308   tv.tv_usec = 100;
00309 
00310   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00311   if(rc==-1) return -1;
00312   if(rc== 2) return -1;
00313 
00314   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00315   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00316   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00317 
00318   if(status3) return -1;
00319   if(((status1) || (status2)) && (rc==1))  return  1;
00320 
00321   return 0;
