00611 {
00612   DIOANDROIDSTREAMBLUETOOTHREMOTEENUMDEVICES* enumeration = (DIOANDROIDSTREAMBLUETOOTHREMOTEENUMDEVICES*)param;
00613   if(!enumeration) return;
