00633     {
00634       if(enumeration->GetEvent()<DIOANDROIDBTENUM_LASTEVENT)
00635         {
00636           enumeration->CheckTransition();
00637 
00638           switch(enumeration->GetCurrentState())
00639             {
00640               case DIOANDROIDBTENUMFSMSTATE_NONE            : break;
00641 
