00720                                 break;
00721 
00722                       default : XTRACE_PRINTCOLOR(1, __L("[type unknown]"));
00723                                 break;
00724 
00725           }
00726        }
00727 
00728     XTRACE_PRINTCOLOR(4, __L("Protocol: %08X %08X %d"), SDP_lastservice->GetID(), protocol->GetType(), protocol->GetChannel());
