00251 int DIOANDROIDSTREAMUDP::IsReadyConnect(int socket)
00252 {
00253   struct timeval  tv;
00254   int             rc;
00255   fd_set          fdr;
00256   fd_set          fdw;
00257   fd_set          fds;
00258 
00259   if(socket==-1) return -1;
00260 
00261   FD_ZERO(&fdr);
00262   FD_ZERO(&fdw);
00263   FD_ZERO(&fds);
00264 
00265   FD_SET(socket,&fdr);
00266   FD_SET(socket,&fdw);
00267   FD_SET(socket,&fds);
00268 
00269   tv.tv_sec  = 0;
00270   tv.tv_usec = 100;
00271 
00272   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00273   if(rc==-1) return -1;
00274 
00275   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00276   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00277   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00278 
00279   if(config->IsServer())
00280     {
00281       if(status1 || status2 ) return  1;
00282       if(status3)             return -1;
00283     }
00284    else
00285     {
00286       if((!status1) && status2) return  1;
00287       if(status3)               return -1;
00288     }
00289 
00290   return 0;
00291 }
