00161 XWORD DIOATCMDGSM_ALPHABET_8859_1::Traslate(bool from, XWORD data)
00162 {
00163   XDWORD end         = (sizeof(table)/sizeof(char[2]));
00164   char  extended   = ((data&0xFF00)>>8);
00165   char  character  = (data&0x00FF);
00166   char  escape     = 0x1B;
00167   XDWORD c;
00168 
00169   if(from)
00170     {
00171       for(c=0;c<127;c++)
00172         {
00173           if(table[c][1]==character) return (XWORD)table[c][0];
00174         }
00175 
00176       for(c=128;c<end;c++)
00177         {
00178           if(table[c][1]==character) return (XWORD)((escape<<8)|table[c][0]);
00179         }
00180     }
00181    else
00182     {
00183       int first =0;
00184 
00185       if(extended==escape)
00186             first = 128;
00187        else end  = 127;
00188 
00189       for(c=first;c<end;c++)
00190         {
00191           if(table[c][0]==character) return (XWORD)table[c][1];
00192         }
00193      }
00194 
00195   return 0xFF00;
00196 }
