00890   int                          value;
00891   XBYTE*                       codec7 = new XBYTE[256];
00892   XBYTE*                       codec8 = new XBYTE[256];
00893   int                          size7  = 0;
00894   int                          size8  = 0;
00895   int                          c;
00896 
00897   if((!codec7) || (!codec8)) return false;
00898 
00899   memset(codec7 ,0 , 256);
00900   memset(codec8 ,0 , 256);
00901 
00902   result.Empty();
00903 
00904   _number  = number;
00905   _message = message;
00906 
00907   // No SMS Center.
00908   result += __L("00");
00909   // SMS-SUBMIT mensaje.
00910   result += __L("11");
00911   result += __L("00");
00912 
00913   // Size Number (National)
00914   string.Format(__L("%02X"),_number.GetSize());
00915   result += string;
00916 
00917   if(_number.GetSize()>9)
00918          result += __L("91");    // Type Number (International);
00919     else result += __L("81");    // Type Number (National);
00920 
00921   // GSM Number
00922   string = number;
00923   if(string.GetSize()%2) string += __L("F");
00924   string.Swab();
00925   result += string;
00926   result += __L("00");
00927 
00928   // Codec 7/8 Bits
00929   if(inoctets)
00930          result += __L("02");
00931     else result += __L("00");
00932 
00933   // Time Max Msg <28 days  >28 weeks
00934   if(validperiod>28)
00935           value = 168 + validperiod;
00936     else  value = 197 + validperiod;
00937   string.Format(__L("%02X"),value);
00938   result += string;
00939 
00940   // Convert message
00941   for(c=0;c<(int)_message.GetSize();c++)
00942     {
00943       XWORD data=alphabet.Traslate(false,(XBYTE)message[c]);
00944       XBYTE byte[2];
00945 
00946       byte[1]=(XBYTE)(data>>8);
00947       byte[0]=(XBYTE)(data&0x00FF);
00948 
00949       if(byte[1])
00950         {
00951           codec8[size8]=byte[1];
00952           size8++;
00953         }
00954 
00955       codec8[size8]=byte[0];
00956       size8++;
00957     }
00958 
00959 
00960   // Compress 7 Bits.
00961   if(!inoctets)
00962     {
00963       int shift=0;
00964 
00965       for(c=0;c<size8;c++)
00966         {
00967           XBYTE mask = (0xFF>>(7-shift));
00968           XBYTE data = (codec8[c+1]&mask)<<(XBYTE)(7-shift);
00969 
00970           if(shift!=7)
00971             {
00972               codec7[size7]=(data|(codec8[c]>>(XBYTE)shift));
00973               size7++;
00974             }
00975 
00976           shift++;
00977           if(shift>7) shift=0;
00978         }
00979     }
00980 
00981   string.Format(__L("%02X"),_message.GetSize());
00982   result += string;
00983 
00984   for(c=0;c<((inoctets)?size8:size7);c++)
00985     {
00986       string.Format(__L("%02X"),(inoctets)?codec8[c]:codec7[c]);
00987       result += string;
00988     }
00989 
00990   delete [] codec7;
00991   delete [] codec8;
00992 
00993   length.Format(__L("%d"),(result.GetSize()/2)-1);
00994 
00995   return true;
00996 }
