00645 int DIOATCMDS::CalculeBaudRateStreamUART(int timeout)
00646 {
00647   int baudrates[] = { 115200, 9600, 57600, 38400, 19200, 4800, 2400 , 1200 };
00648   int baudrate    = 0;
00649 
00650   DIOSTREAMUARTCONFIG* diostreamcfg = (DIOSTREAMUARTCONFIG*)diostream->GetConfig();
00651   if(!diostreamcfg) return 0;
00652 
00653   if(diostreamcfg->GetType() != DIOSTREAMTYPE_UART) return 0;
00654 
00655   for(int c=0;c<sizeof(baudrates)/sizeof(int);c++)
00656     {
00657       diostreamcfg->SetBaudRate(baudrates[c]);
00658 
00659       //XTRACE_PRINT(__L("Open connection: %d bauds"),baudrates[c]);
00660 
00661       diostream->ResetXBuffers();
00662 
00663       diostream->Close();
00664       if(!diostream->Open())  return 0;
00665 
00666       if(diostream->WaitToConnected(timeout))
00667         {
00668           if(threadconnection) threadconnection->Ini();
00669 
00670           echomode = false;
00671           if(Basic(timeout)==DIOATCMD_ERROR_NONE) baudrate = baudrates[c];
00672 
00673           echomode = true;
00674           if(Basic(timeout)==DIOATCMD_ERROR_NONE)  baudrate = baudrates[c];
00675 
00676           if(threadconnection) threadconnection->End();
00677 
00678           if(baudrate) break;
00679 
00680         }
00681     }
00682 
00683   diostream->Close();
00684 
00685   return baudrate;
