01407 int DIOATCMDS::SearchInAnswers(XCHAR* string, int timeout)
01408 {
01409   if(timeout) xtimer->Reset();
01410 
01411   while(1)
01412     {
01413       for(int c=0;c<(int)answers.GetSize();c++)
01414         {
01415           XSTRING* answer = (XSTRING*)answers.Get(c);
01416           if(answer)
01417             {
01418               if(answer->Find(string,true)!=XSTRING_NOTFOUND) return c;
01419             }
01420         }
01421 
01422       if(timeout)
01423         {
01424           if((int)xtimer->GetMeasureSeconds()>=timeout)
01425             {
01426               return -1;
01427 
01428             } else GEN_XSLEEP.MilliSeconds(10);
01429 
01430         } else break;
01431 
01432     }
01433 
01434   return DIOATCMDS_ANSWERNOTFOUND;
01435 }
