01450 int DIOATCMDS::SearchInAnswers(XDWORD typecommand,int timeout)
01451 {
01452   DIOATCMD* command = GetCommand(typecommand);
01453   if(!command) return DIOATCMDS_ANSWERNOTFOUND;
01454 
01455   XSTRING string;
01456 
01457   string = command->GetCommand();
01458   string += __L(":");
01459 
01460   return SearchInAnswers(string.Get(),timeout);
01461 }
