01463 
01476 DIOATCMD_ERROR DIOATCMDS::WaitToProcessAnswer(int timeout, int* index)
01477 {
01478   if(!xtimer) return DIOATCMD_ERROR_UNKNOWN;
01479 
01480   xtimer->Reset();
01481 
01482   do{ for(int c=0;c<(int)answers.GetSize();c++)
01483         {
01484           XSTRING* answer = (XSTRING*)answers.Get(c);
01485           if(answer)
01486             {
01487               if((!answer->Compare(__L("OK")))  || (!answer->Compare(__L("0"))))
01488                 {
01489                   if(index) (*index) = c;
01490                   DeleteAnswer(c);
01491                   return DIOATCMD_ERROR_NONE;
01492                 }
01493 
01494               if((!answer->Compare(__L("ERROR")))  || (!answer->Compare(__L("1"))))
01495                 {
01496                   if(index) (*index) = c;
01497                   DeleteAnswer(c);
01498                   return DIOATCMD_ERROR_INVALIDCMD;
01499                 }
01500             }
01501         }
01502 
01503       if((int)xtimer->GetMeasureSeconds()>=timeout)
01504         {
01505           return DIOATCMD_ERROR_TIMEOUT;
01506 
01507         } else GEN_XSLEEP.MilliSeconds(50);
01508 
01509     } while(1);
01510 
01511   return DIOATCMD_ERROR_UNKNOWN;
01512 }
01513 
01514 
01526 DIOATCMD_ERROR DIOATCMDS::WaitToOneAnswerMore(int timeout)
01527 {
01528   if(!xtimer) return DIOATCMD_ERROR_UNKNOWN;
01529 
01530   int actualnanswer = (int)answers.GetSize();
01531 
01532   xtimer->Reset();
01533 
01534   do{ if((int)xtimer->GetMeasureSeconds()>=timeout)
01535         {
01536           return DIOATCMD_ERROR_TIMEOUT;
01537 
01538         } else GEN_XSLEEP.MilliSeconds(50);
01539 
01540     } while(actualnanswer == (int)answers.GetSize());
01541 
01542   return DIOATCMD_ERROR_NONE;
01543 }
