01567 
01582 DIOATCMD_ERROR DIOATCMDS::MakeCommandWithSimpleAnswer(XDWORD type, XSTRING& answer, XCHAR* param, int timeout)
01583 {
01584   DIOATCMD_ERROR error = SendCommand(type);
01585   if(error!=DIOATCMD_ERROR_NONE) return error;
01586 
01587   int index=0;
01588 
01589   error = WaitToProcessAnswer(timeout,&index);
01590   if(error!=DIOATCMD_ERROR_NONE) return error;
01591 
01592   XSTRING* _answer = GetAnswer(index-1);
01593   if(_answer)
01594     {
01595       answer = (*_answer);
01596       DeleteAnswer(index-1);
01597     }
01598 
01599   return DIOATCMD_ERROR_NONE;
01600 }
