01602 
01612 void DIOATCMDS::ThreadConnection(void* param)
01613 {
01614   DIOATCMDS* dioatcmds = (DIOATCMDS*)param;
01615   if(!dioatcmds) return;
01616 
01617   DIOSTREAM* diostream = dioatcmds->GetDIOStream();
01618   if(!diostream) return;
01619 
01620   if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return;
01621 
01622   XBUFFER* bufferread = diostream->GetInXBuffer();
01623   if(bufferread)
01624     {
01625       if(bufferread->GetSize())
01626         {
01627           if(dioatcmds->mutexreadwriteprocess) dioatcmds->mutexreadwriteprocess->Lock();
01628 
01629           XSTRING answer;
01630 
01631           if(dioatcmds->ReadAnswer(answer))
01632             {
01633               if(!answer.IsEmpty())
01634                 {
01635                   if(dioatcmds->echomode)
01636                     {
01637                       int index = dioatcmds->SearchSendCommand(&answer);
01638                       if(index!=DIOATCMDS_COMMANDNOTFOUND)
01639                         {
01640                           dioatcmds->DeleteSendCommand(index);
01641                           if(dioatcmds->mutexreadwriteprocess) dioatcmds->mutexreadwriteprocess->UnLock();
01642                           return;
01643                         }
01644                     }
01645 
01646                   if(!dioatcmds->CheckUnsolicitedAnswer(answer))
01647                     {
01648                       XSTRING* answerkeep = new XSTRING();
01649                       if(answerkeep)
01650                         {
01651                           (*answerkeep) = answer;
01652                           dioatcmds->answers.Add(answerkeep);
01653                         }
01654                     }
01655                 }
01656             }
01657 
01658           if(dioatcmds->mutexreadwriteprocess) dioatcmds->mutexreadwriteprocess->UnLock();
01659         }
01660     }
01661 }
01662 
01663 
01675 bool DIOATCMDS::CheckUnsolicitedAnswer(XSTRING& answer)
01676 {
01677   if(answer.IsEmpty())  return false;
01678   if(ATcmds.IsEmpty())   return false;
01679 
01680   for(int c=0;c<(int)ATcmds.GetSize();c++)
01681     {
01682       DIOATCMD* atcmd = (DIOATCMD*)ATcmds.Get(c);
01683       if(atcmd)
01684         {
01685           if(atcmd->IsUnSolicited())
01686             {
01687               int index = answer.Find(atcmd->GetCommand(),false);
01688               if(index!=XSTRING_NOTFOUND)
01689                 {
01690                   XSTRING                      param;
01691                   XBUFFER                      additionalparam;
01692                   DIOATCMD_TYPEADDITIONALSIZE  typesize = DIOATCMD_TYPEADDITIONALSIZE_NONE;
01693                   int                          sizedata = 0;
01694 
01695                   answer.Copy(index+atcmd->GetCommandString()->GetSize()+1,param);
01696 
01697                   if(UnsolicitedCommandAddtionalSize(atcmd->GetType(),param,typesize,sizedata))
01698                     {
01699                       switch(typesize)
01700                         {
01701                           case DIOATCMD_TYPEADDITIONALSIZE_NONE   : break;
01702 
01703                           case DIOATCMD_TYPEADDITIONALSIZE_BYTES  : additionalparam.Resize(sizedata);
01704                                                                     diostream->Read((XBYTE*)additionalparam.Get(),sizedata);
01705                                                                     break;
01706 
01707                           case DIOATCMD_TYPEADDITIONALSIZE_LINES  : { XSTRING line;
01708                                                                       int     d = 0;
01709 
01710                                                                       while(d<sizedata)
01711                                                                         {
01712                                                                           if(ReadAnswer(line))
01713                                                                             {
01714                                                                               if(!line.IsEmpty())
01715                                                                                 {
01716                                                                                   XBUFFER charstr;
01717                                                                                   
01718                                                                                   line.ConvertToASCII(charstr);
01719                                                                                   additionalparam.Add((XBYTE*)charstr.Get(), (XDWORD)line.GetSize());
01720                                                                                   
01721                                                                                   d++;
01722                                                                                 }
01723                                                                             }
01724 
01725                                                                         }
01726                                                                     }
01727                                                                     break;
01728                         }
01729 
01730                       return UnsolicitedCommand(atcmd->GetType(),param,additionalparam);
01731 
01732                     } else return false;
01733                 }
01734             }
01735         }
01736     }
01737 
01738   return false;
01739 }
