00421 XDWORD DIOALERT::CalculateID(bool withdatetime)
00422 {
00423   if(!xdatetime)  return false;
00424 
00425   HASHCRC32* hashcrc32 = new HASHCRC32();
00426   if(!hashcrc32) return 0;
00427 
00428   XSTRING IDstring;
00429   XSTRING levelstring;
00430   XDWORD  ID = 0;
00431   bool    status;
00432 
00433   if(withdatetime) xdatetime->GetDateTimeToString(XDATETIME_FORMAT_STANDARD, IDstring);
00434 
00435   levelstring.Format(__L("%s %d"), applicationID.Get(), level);
00436   IDstring += levelstring;
00437 
00438   XRAND* GEN_XFACTORY_CREATE(xrand, CreateRand())
00439   if(xrand)
00440     {
00441       levelstring.Format(__L(" %d "), xrand->Between(1000, 10000000) * xrand->Between(1000, 10000000));
00442       GEN_XFACTORY.DeleteRand(xrand);
00443     }
00444 
00445   IDstring += __L(" ");   IDstring += origin;
00446   IDstring += __L(" ");   IDstring += title;
00447   IDstring += __L(" ");   IDstring += message;
00448   IDstring += levelstring;
00449 
00450   XBUFFER charstr;
00451   
00452   IDstring.ConvertToASCII(charstr);         
00453   status = hashcrc32->Do(charstr.Get(), IDstring.GetSize());
00454 
00455   if(status) ID = hashcrc32->GetResultCRC32(); else ID = 0;
00456 
00457   delete hashcrc32;
00458 
00459   return ID;
00460 }
