00913 bool DIOALERTS::Sender_SMTPConfig(XCHAR* URL, int port, XCHAR* login, XCHAR* password, XCHAR* senderemail, int nrecipients,...)
00914 {
00915   if(!isinitialized) return false;
00916 
00917   if(!SMTP) return false;
00918 
00919   SMTPsenderisactive = false;
00920 
00921   SMTP->Server_GetURL()->Set(URL);
00922   SMTP->Server_SetPort(port);
00923   SMTP->Server_GetLogin()->Set(login);
00924   SMTP->Server_GetPassword()->Set(password);
00925   SMTP->GetSenderEmail()->Set(senderemail);
00926   SMTP->SetContentType(DIOSSMPTCONTENTTYPE_UTF8);
00927 
00928   va_list  arg;
00929 
00930   va_start(arg, nrecipients);
00931 
00932   int nr = nrecipients;
00933 
00934   while(nr)
00935     {
00936       XCHAR* recipient = (XCHAR*)va_arg(arg, XCHAR*);
00937       if(!recipient) break;
00938 
00939       DIOSMTPRECIPIENTTYPE  type        = DIOSMTPRECIPIENTTYPE_UNKNOWN;
00940       int                   test        = 0;
00941       XSTRING               recipientstring;
00942       XSTRING               name;
00943       XSTRING               email;
00944 
00945       recipientstring = recipient;
00946 
00947       name.AdjustSize(_MAXSTR);
00948       email.AdjustSize(_MAXSTR);
00949 
00950       recipientstring.UnFormat(__L("%d,%s,%s,%d"), &type, name.Get(), email.Get(), &test);
00951 
00952       name.AdjustSize();
00953       email.AdjustSize();
00954 
00955       if((type == DIOSMTPRECIPIENTTYPE_TO) || (type == DIOSMTPRECIPIENTTYPE_CC) || (type == DIOSMTPRECIPIENTTYPE_BCC))
00956         {
00957           SMTP->AddRecipient(type, name , email, test?true:false);
00958         }
00959 
00960       nr--;
00961     }
00962 
00963   va_end(arg);
00964 
00965   SMTPsenderisactive = true;
00966 
00967   return SMTPsenderisactive;
00968 }
