01174 bool DIOALERTS::IsValidConditionToSend(XDWORD conditionID)
01175 {
01176   bool mustsend   = true;
01177 
01178   DIOALERT_CONDITION* condition = GetCondition(conditionID);
01179   if(condition)
01180     {
01181       //-------------------------------------------------------------
01182       // Firts send
01183 
01184       if(!condition->GetNSended())
01185         {
01186           condition->GetLastTimerSend()->Reset();
01187         }
01188        else 
01189         {
01190           if(condition->GetTimeLimitForRepeat())
01191             {
01192               if((XDWORD)condition->GetTimeLimitForRepeat() > condition->GetLastTimerSend()->GetMeasureSeconds())
01193                 {
01194                   mustsend = false;
01195                 }
01196             }
01197         
01198           if(condition->GetEveryNumberOfTimes())
01199             {
01200               if(condition->GetNTimesSended() < condition->GetEveryNumberOfTimes())   
01201                 {              
01202                   mustsend = false;  
01203                 } 
01204             }
01205         }    
01206     }
01207 
01208   return mustsend;
01209 }
