01224 int DIOALERTS::Send(DIOALERTSENDER sender, XDWORD conditionID, DIOALERT* alert)
01225 {
01226   if(!isinitialized) return false;
01227 
01228   int   statussend = 0;
01229 
01230   xmutexsending->Lock();  
01231 
01232   if(IsValidConditionToSend(conditionID))
01233     {      
01234       issending = true;
01235 
01236       if((sender & DIOALERTSSENDER_SMPT) && (SMTPsenderisactive)) statussend |= Sender_SMTPSend(alert)? DIOALERTSSENDER_SMPT: 0;
01237       if((sender & DIOALERTSSENDER_SMS)  && (SMSsenderisactive))  statussend |= Sender_SMSSend(alert) ? DIOALERTSSENDER_SMS : 0;
01238       if((sender & DIOALERTSSENDER_WEB)  && (WEBsenderisactive))  statussend |= Sender_WEBSend(alert) ? DIOALERTSSENDER_WEB : 0;
01239       if((sender & DIOALERTSSENDER_UDP)  && (UDPsenderisactive))  statussend |= Sender_UDPSend(alert) ? DIOALERTSSENDER_UDP : 0;
01240       
01241       issending = false;      
01242     }
01243 
01244   DIOALERT_CONDITION* condition = GetCondition(conditionID);
01245   if(condition)
01246     {
01247        if(condition->GetNTimesSended() >= condition->GetEveryNumberOfTimes())   
01248          {              
01249            condition->SetNTimesSended(0);
01250          }
01251 
01252       if(statussend) condition->GetLastTimerSend()->Reset();
01253       condition->SetNSended(condition->GetNSended()+1);
01254       condition->SetNTimesSended(condition->GetNTimesSended()+1);
01255     }
01256 
01257   xmutexsending->UnLock();
01258 
01259   return statussend;
01260 }
